package com.dev.intourist.ui.screen.contactsimport android.os.Bundleimport android.view.Viewimport androidx.navigation.fragment.findNavControllerimport com.dev.intourist.Rimport com.dev.intourist.databinding.FragmentContactsBindingimport com.dev.intourist.ui.base.fragment.BaseFragmentimport org.koin.androidx.viewmodel.ext.android.viewModelimport by.kirich1409.viewbindingdelegate.viewBindingimport com.dev.intourist.ui.screen.contacts.ContactsViewModelclass ContactsFragment :    BaseFragment<FragmentContactsBinding, ContactsViewModel>(R.layout.fragment_contacts) {    override val binding: FragmentContactsBinding by viewBinding(FragmentContactsBinding::bind)    override val viewModel: ContactsViewModel by viewModel()    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        binding.apply {            tvContacts.setNavigationOnClickListener {                findNavController().popBackStack()            }        }    }}