package com.dev.intourist.ui.screen.payment.by_cardimport android.os.Bundleimport androidx.fragment.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.navigation.fragment.findNavControllerimport com.dev.intourist.Rimport com.dev.intourist.databinding.FragmentByCardBindingclass ByCardFragment : Fragment() {    private val binding : FragmentByCardBinding by lazy {        FragmentByCardBinding.inflate(layoutInflater)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)    }    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        binding.apply {            buttonBack.setOnClickListener {                findNavController().popBackStack()            }            buttonContinue.setOnClickListener {                findNavController().navigate(R.id.SuccessFragment)            }        }    }}