package com.dev.intourist.ui.screen.payment.successimport android.os.Bundleimport androidx.fragment.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.navigation.fragment.findNavControllerimport com.dev.intourist.Rimport com.dev.intourist.databinding.FragmentSuccessBindingclass SuccessFragment : Fragment() {    private val binding : FragmentSuccessBinding by lazy {        FragmentSuccessBinding.inflate(layoutInflater)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)    }    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View {        // Inflate the layout for this fragment        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        binding.apply {            buttonContinue.setOnClickListener {                findNavController().navigate(R.id.fragment_home)            }        }    }}