package com.dev.intourist.ui.screen.payment.withChildrenimport android.content.Intentimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.fragment.app.Fragmentimport androidx.navigation.fragment.findNavControllerimport com.dev.intourist.Rimport com.dev.intourist.databinding.FragmentWithChildrenBindingimport com.dev.intourist.ui.screen.payment.other_way.OtherWayFragmentclass WithChildrenFragment : Fragment() {    private lateinit var binding: FragmentWithChildrenBinding    private var adultsCount = 1    private var babyCount = 0    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        binding = FragmentWithChildrenBinding.inflate(inflater, container, false)        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        binding.apply {            buttonContinue.setOnClickListener {                findNavController().navigate(R.id.byCardFragment)            }            buttonBack.setOnClickListener {                findNavController().popBackStack()            }            imgConfirm.setOnClickListener {                startActivity(Intent(requireActivity(), OtherWayFragment::class.java))            }            imgCard.setOnClickListener {                startActivity(Intent(requireActivity(), OtherWayFragment::class.java))            }            imgPayment.setOnClickListener {                startActivity(Intent(requireActivity(), OtherWayFragment::class.java))            }            btnCountAdultsPlus.setOnClickListener {                adultsCount++                updateAdultsCount()            }            btnCountAdultsMinus.setOnClickListener {                if (adultsCount > 1) {                    adultsCount--                    updateAdultsCount()                }            }            btnCountBabyPlus.setOnClickListener {                babyCount++                updateBabyCount()            }            btnCountBabyMinus.setOnClickListener {                if (babyCount > 0) {                    babyCount--                    updateBabyCount()                }            }            updateAdultsCount()            updateBabyCount()        }    }    private fun updateAdultsCount() {        binding.txtResult.text = adultsCount.toString()    }    private fun updateBabyCount() {        binding.txtResult2.text = babyCount.toString()    }}