package com.dev.intourist.ui.screen.payment.withoutChildrenimport android.os.Bundleimport androidx.fragment.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.navigation.fragment.findNavControllerimport com.dev.intourist.Rimport com.dev.intourist.databinding.FragmentWithoutChildrenBindingclass WithoutChildrenFragment : Fragment() {    private val binding : FragmentWithoutChildrenBinding by lazy {        FragmentWithoutChildrenBinding.inflate(layoutInflater)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        onBtnClick()    }    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        // Inflate the layout for this fragment        return binding.root    }    private fun onBtnClick(){        binding.buttonContinue.setOnClickListener {            findNavController().navigate(R.id.byCardFragment)        }    }}